mapper.select:
    	select ding.* from (
			SELECT du.user_name, di.* FROM oa_dingding di
			LEFT JOIN oa_dingding_user du on di.user_id = du.user_id
		) ding
		
			and ding.user_id = #{userId}
			and ding.user_name = #{userName}
			 and ding.userCheckTime between #{startTime} and #{endTime}
			and ding.checkType= #{checkType}

mapper.update:
update oa_dingding
workDate = #{workDate}, checkType = #{checkType}, userCheckTime = #{userCheckTime}, timeResult = #{timeResult}, status = #{status},
where user_id = #{userId}

update oa_dingding 
set timeResult=#{timeResult},status=#{status} 
where user_id= #{userId}
and userCheckTime   >= #{startTime}  and userCheckTime  <= #{endTime}  
and workDate = #{workDate}
and checkType = #{checkType}

mapper.select:
SELECT ding.* FROM ( SELECT u.dept_id, u.`user_id`, u.`user_name` FROM `sys_user` u ) nu RIGHT JOIN ( SELECT ouser.user_id, us.user_name, ding.workDate, ( CASE WHEN ding.checkType='OnDuty' THEN '上班' ELSE '下班' END )checkType, ding.userCheckTime, ( CASE WHEN ding.timeResult='Normal' THEN '正常' WHEN ding.timeResult='Late' THEN '迟到' WHEN ding.timeResult='Early' THEN '早退' WHEN ding.timeResult='SeriousLate' THEN '严重迟到' WHEN ding.timeResult='NotSigned' THEN '未打卡' WHEN ding.timeResult = 'out' THEN '外出' WHEN ding.timeResult = 'Absenteeism' THEN '旷工迟到' WHEN ding.timeResult = '1' THEN '年假' WHEN ding.timeResult = '2' THEN '调休' WHEN ding.timeResult = '3' THEN '事假' WHEN ding.timeResult = '4' THEN '病假' WHEN ding.timeResult = '5' THEN '婚假' WHEN ding.timeResult = '6' THEN '产假' WHEN ding.timeResult = '7' THEN '陪产假' WHEN ding.timeResult = '8' THEN '产检假' WHEN ding.timeResult = '9' THEN '丧假' WHEN ding.timeResult = '10' THEN '哺乳假' ELSE '' END ) timeResult, ding.timeResult timeResult2 FROM `oa_dingding` ding LEFT JOIN oa_dingding_user us ON ding.user_id = us.user_id LEFT JOIN sys_user ouser ON ouser.user_name = us.user_name ) ding ON ding.user_id = nu.user_id
and ding.user_id = #{userId} and ding.user_name like concat('%', #{userName}, '%') and ding.workDate = #{workDate} and ding.checkType = #{checkType} and ding.timeResult2 = #{timeResult}
and nu.dept_id in
#{item.deptId}
AND date_format(ding.workDate,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(ding.workDate,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
