mapper.select:
    	select * from oa_dingding
		
			and user_id = #{userId}
			and user_name = #{userName}
			 and userCheckTime between #{startTime} and #{endTime}
			and checkType = #{checkType}
			and workDate = #{workDate}
		 
		ORDER BY checkType DESC

mapper.update:
update oa_dingding
user_name = #{userName}, workDate = #{workDate}, checkType = #{checkType}, userCheckTime = #{userCheckTime}, timeResult = #{timeResult}, status = #{status}, #{applyState},
where user_id = #{userId}

update oa_dingding 
set timeResult=#{timeResult},status=#{status},apply_state=#{applyState} 

	user_id = #{userId}
	user_name = #{userName}
	and userCheckTime   >= #{startTime}  and userCheckTime  <= #{endTime}  
	and workDate = #{workDate}
	and checkType = #{checkType}

mapper.select:
SELECT a.* FROM ( SELECT ding.* FROM ( SELECT u.dept_id, u.`user_id`, u.`user_name` FROM `sys_user` u ) nu RIGHT JOIN ( SELECT ouser.user_id, ding.user_name, ding.workDate, ding.weekDay, ( CASE WHEN ding.checkType='OnDuty' THEN '上班' ELSE '下班' END )checkType, ding.userCheckTime, ( CASE WHEN ding.timeResult='Normal' THEN '正常' WHEN ding.timeResult='Late' THEN '迟到' WHEN ding.timeResult='Early' THEN '早退' WHEN ding.timeResult='SeriousLate' THEN '严重迟到' WHEN ding.timeResult='NotSigned' THEN '未打卡' WHEN ding.timeResult = 'out' THEN '外出' WHEN ding.timeResult = 'Absenteeism' THEN '旷工迟到' WHEN ding.timeResult = '1' THEN '年假' WHEN ding.timeResult = '2' THEN '调休' WHEN ding.timeResult = '3' THEN '事假' WHEN ding.timeResult = '4' THEN '病假' WHEN ding.timeResult = '5' THEN '婚假' WHEN ding.timeResult = '6' THEN '产假' WHEN ding.timeResult = '7' THEN '陪产假' WHEN ding.timeResult = '8' THEN '产检假' WHEN ding.timeResult = '9' THEN '丧假' WHEN ding.timeResult = '10' THEN '哺乳假' ELSE '' END ) timeResult, ding.timeResult timeResult2, d.dept_name, ouser.area, ouser.intime, (CASE WHEN ding.apply_state = 1 THEN '待审批' WHEN ding.apply_state = 2 THEN '已撤回' WHEN ding.apply_state = 3 THEN '申请成功' WHEN ding.apply_state = 4 THEN '申请失败' WHEN ding.apply_state = 5 THEN '已销假' ELSE '' END)apply_state, (CASE WHEN ding.status = 1 THEN '异常' ELSE '正常' END)status FROM `oa_dingding` ding LEFT JOIN sys_user ouser ON ouser.user_name = ding.user_name LEFT JOIN sys_dept d on d.dept_id = ouser.dept_id ) ding ON ding.user_id = nu.user_id
and ding.user_id = #{userId} and ding.user_name like concat('%', #{userName}, '%') and ding.workDate = #{workDate} and ding.checkType = #{checkType} and ding.dept_name = #{deptName} and ding.timeResult2 = #{timeResult} and ding.status = #{status} and ding.area = #{area}
and nu.dept_id in
#{item.deptId}
AND date_format(ding.workDate,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(ding.workDate,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
) a ORDER BY a.user_id,a.intime,a.userCheckTime
