mapper.select:
select u.user_id, u.dept_id, u.login_name, u.user_name,u.area, u.email, u.phonenumber, u.password, u.sex, u.avatar, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark,u.record,u.intime,u.firstphase,u.secondphase, d.dept_name from sys_user u
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

	AND u.user_id = #{userId}


	AND u.login_name like concat('%', #{loginName}, '%')


	AND u.user_name like concat('%', #{userName}, '%')


	AND u.area = #{area}


	AND u.status = #{status}


	AND u.phonenumber like concat('%', #{phonenumber}, '%')


	AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


	AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


	AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))


	AND u.record = #{record}


	AND u.intime = #{intime}


	AND u.firstphase = #{firstphase}


	AND u.secondphase = #{secondphase}


${params.dataScope}

select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.area, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}
