mapper.select:
    	SELECT us.user_name,a.* 
    		FROM sys_user_apply a
			LEFT JOIN sys_user us ON us.user_id = a.user_id 
		
		      and a.apply_type = #{applyType}
			  and a.apply_state = #{applyState}
			 AND a.STATUS = #{status}

select a.apply_id, a.user_id, a.list_num, a.starttime, a.endtime, a.timeapart1, a.timeapart2, a.timeapart3, a.for_applyid, a.prove, a.timelength, (CASE WHEN a.apply_type = 1 THEN CONCAT(TRUNCATE(a.timelength,1 ), '天') WHEN a.apply_type = 2 THEN CONCAT(TRUNCATE(a.timelength,0 ) , '小时') WHEN a.apply_type = 3 THEN CONCAT(TRUNCATE(a.timelength,1 ) , '天') ELSE '' END)timelengthPlus, (CASE WHEN a.apply_type = 1 THEN '请假' WHEN a.apply_type = 2 THEN '加班' WHEN a.apply_type = 3 THEN '销假' ELSE '取消请假' END)apply_type, (CASE WHEN a.apply_state = 1 THEN '待审批' WHEN a.apply_state = 2 THEN '已撤回' WHEN a.apply_state = 3 THEN '申请成功' WHEN a.apply_state = 4 THEN '申请失败' WHEN a.apply_state = 5 THEN '已销假' ELSE '' END)apply_state, (CASE WHEN a.confirm_state = 0 THEN '未确认' WHEN a.confirm_state = 1 THEN '已确认' ELSE '' END)confirm_state, a.details, (CASE WHEN a.leave_type = 1 THEN '年假' WHEN a.leave_type = 2 THEN '调休' WHEN a.leave_type = 3 THEN '事假' WHEN a.leave_type = 4 THEN '病假' WHEN a.leave_type = 5 THEN '婚假' WHEN a.leave_type = 6 THEN '产假' WHEN a.leave_type = 7 THEN '陪产假' WHEN a.leave_type = 8 THEN '产检假' WHEN a.leave_type = 9 THEN '丧假' ELSE '' END)leave_type, a.apply_time, p.approval_id, p.apply_id, p.approval_state, p.approver_id, (p.approval_sight)approval_s ,p.approval_time, u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u.sex, u.avatar, u.password, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_time, u.remark,u.record,u.intime,u.firstphase,u.secondphase, (s.user_name)approver_name from sys_user_apply a LEFT JOIN sys_user_approval p ON p.apply_id = a.`apply_id` LEFT JOIN sys_user u ON u.`user_id` = a.`user_id` LEFT JOIN sys_user s ON s.user_id = p.approver_id
and a.user_id = #{userId} and a.list_num like concat('%', #{listNum}, '%') and a.timelength = #{timelength} and a.apply_type = #{applyType} and a.apply_state = #{applyState} and a.confirm_state = #{confirmState} and a.leave_type = #{leaveType} and a.apply_time = #{applyTime} and a.prove = #{prove} and s.user_name = #{approverName} and a.for_applyid = #{forApplyId} and p.approval_sight = #{approvalS}
ORDER BY a.apply_id DESC

mapper.update:
update sys_user_apply
user_id = #{userId}, list_num = #{listNum}, starttime = #{starttime}, endtime = #{endtime}, timeapart1 = #{timeapart1}, timeapart2 = #{timeapart2}, timeapart3 = #{timeapart3}, timelength = #{timelength}, apply_type = #{applyType}, apply_state = #{applyState}, confirm_state = #{confirmState}, details = #{details}, leave_type = #{leaveType}, apply_time = #{applyTime}, prove = #{prove} for_applyid = #{forApplyId}, status = #{status},
where apply_id = #{applyId}

mapper.select:
SELECT sum(timelength) FROM sys_user_apply 

leave_type IN (3 , 4 ) 
 and user_id = #{userId}
 and starttime >= #{starttime} 
 and endtime <= #{endtime} 
and apply_type = #{applyType}
AND apply_state = 3

SELECT * FROM sys_user_apply 

leave_type IN (3 , 4 ) 
 and user_id = #{userId}
 and starttime < #{starttime} 
 and endtime <= #{endtime} 
and apply_type = #{applyType}
AND apply_state = 3

SELECT * FROM sys_user_apply 

leave_type IN (3 , 4 ) 
 and user_id = #{userId}
 and starttime >= #{starttime} 
 and endtime > #{endtime} 
and apply_type = #{applyType}
AND apply_state = 3

		SELECT * FROM sys_user_apply 
 	
 	leave_type = 6 AND apply_state = 3 
 	 and user_id = #{userId}
 	 and DATE_FORMAT(starttime, '%Y-%m') <= #{timeapart1} 
 	 and DATE_FORMAT(endtime, '%Y-%m') >= #{timeapart1}

select a.apply_id, a.user_id, a.list_num, a.starttime, a.endtime, a.timeapart1, a.timeapart2, a.timeapart3, a.timelength, a.apply_type, a.apply_state, a.confirm_state, a.details, a.leave_type, a.apply_time,a.for_applyid,a.prove
from sys_user_apply a		

      and a.user_id = #{userId}
       and starttime <= #{starttime} 
      and a.endtime >= #{starttime} 
      and a.apply_type = #{applyType}
     and a.apply_state in (1,3)
      and a.leave_type = #{leaveType}
      and a.timeapart1 = #{timeapart1}
      and a.timeapart2 = #{timeapart2}

select a.apply_id, a.user_id, a.list_num, a.starttime, a.endtime, a.timeapart1, a.timeapart2, a.timeapart3, a.timelength, a.apply_type, a.apply_state, a.confirm_state, a.details, a.leave_type, a.apply_time,a.for_applyid,a.prove
from sys_user_apply a	

      and a.user_id = #{userId}
       and starttime <= #{endtime} 
       and a.endtime >= #{endtime} 
      and a.apply_type = #{applyType}
     and a.apply_state in (1,3)
      and a.leave_type = #{leaveType}
