mapper.select:
SELECT p.approval_id, p.apply_id, (CASE WHEN p.approval_state = 1 THEN '同意' WHEN p.approval_state = 2 THEN '驳回' WHEN p.approval_state = 3 THEN '未操作' ELSE '' END)approval_state, p.approver_id, p.approval_sight, p.approval_time, p.approval_level, p.remark, a.apply_id, a.user_id, a.list_num, a.starttime, a.endtime, a.timeapart1, a.timeapart2, a.timelength, a.for_applyid, (CASE WHEN a.apply_type = 1 THEN '请假' WHEN a.apply_type = 2 THEN '加班' WHEN a.apply_type = 3 THEN '销假' ELSE '' END)apply_type, (CASE WHEN a.apply_state = 1 THEN '待审批' WHEN a.apply_state = 2 THEN '已撤回' WHEN a.apply_state = 3 THEN '申请成功' WHEN a.apply_state = 4 THEN '申请失败' WHEN a.apply_state = 5 THEN '已销假' ELSE '' END)apply_state, a.confirm_state, a.details, a.leave_type, a.apply_time, u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u.sex, u.avatar, u.password, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_time, u.remark,u.record,u.intime,u.firstphase,u.secondphase, d.dept_name FROM sys_user_approval p LEFT JOIN sys_user_apply a ON p.apply_id = a.`apply_id` LEFT JOIN sys_user u ON u.`user_id` = a.`user_id` LEFT JOIN sys_dept d ON u. dept_id = d.dept_id
and p.approval_id = #{approvalId} and p.apply_id = #{applyId} and p.approval_state = #{approvalState} and p.approver_id = #{approverId} and p.approval_sight = 1 and a.apply_state = 1 and p.approval_level = #{approvalLevel} and p.remark = #{remark}
ORDER BY a.apply_id DESC

select a.list_num, a.timelength, (a.user_id)applyer_id, a.for_applyid, (CASE WHEN a.apply_type = 1 THEN CONCAT(TRUNCATE(a.timelength,1 ), '天') WHEN a.apply_type = 2 THEN CONCAT(TRUNCATE(a.timelength,0 ) , '小时') WHEN a.apply_type = 3 THEN CONCAT(TRUNCATE(a.timelength,1 ) , '天') ELSE '' END)timelengthPlus, (CASE WHEN a.apply_type = 1 THEN '请假' WHEN a.apply_type = 2 THEN '加班' WHEN a.apply_type = 3 THEN '销假' ELSE '' END)apply_type, (CASE WHEN a.apply_state = 1 THEN '待审批' WHEN a.apply_state = 2 THEN '已撤回' WHEN a.apply_state = 3 THEN '申请成功' WHEN a.apply_state = 4 THEN '申请失败' WHEN a.apply_state = 5 THEN '已销假' ELSE '' END)apply_state, a.leave_type, a.apply_time, (CASE WHEN p.approval_state = 1 THEN '同意' WHEN p.approval_state = 2 THEN '驳回' WHEN p.approval_state = 3 THEN '未审批' ELSE '' END)approval_state, p.apply_id, p.approval_time, p.approval_sight, p.approval_id, p.approver_id, (u.user_name)applyer_name, (s.user_name)approver_name, d.dept_name from sys_user_approval p LEFT JOIN sys_user_apply a ON p.apply_id = a.`apply_id` LEFT JOIN sys_user u ON u.`user_id` = a.`user_id` LEFT JOIN sys_dept d ON u. dept_id = d.dept_id LEFT JOIN sys_user s ON s.user_id = p.approver_id
and p.approver_id = #{approverId} and p.approval_state = #{approvalState} and a.apply_state = #{applyState} and u.user_name like concat('%', #{applyerName}, '%') and a.timelength = #{timelength} and a.apply_type = #{applyType} and p.approval_time = #{approvalTime} and s.user_name like concat('%', #{approverName}, '%') and a.list_num = #{listNum} and p.approval_id = #{approvalId} and a.user_id = #{applyerId} and d.dept_name = #{deptName} and p.approval_sight = #{approvalSight}
ORDER BY a.apply_id DESC

mapper.update:
update sys_user_approval
apply_id = #{applyId}, approval_state = #{approvalState}, approver_id = #{approverId}, approval_sight = #{approvalSight}, approval_level = #{approvalLevel}, remark = #{remark}, approval_time = #{approvalTime},
where approval_id = #{approvalId}
