mapper.select:


	 and id = #{id}
	 and a.sort =#{sort}
	 and a.submit_type = #{submitType}
	 and a.assets_code like concat('%', #{assetsCode}, '%')
	 and a.region = #{region}
	 and a.assets_name like concat('%', #{assetsName}, '%') 
	 and a.assets_type = #{assetsType}
	 and a.assets_type2 = #{assetsType2}
	 and a.assets_model = #{assetsModel}
	 and a.attach = #{attach}
	 and a.category = #{category}
	 and a.brand = #{brand}
	 and su.user_name like concat('%', #{purchaseName}, '%')
	 and user.user_name like concat('%', #{useName}, '%')


		AND date_format(a.buy_date,'%y%m%d') >=
		date_format(#{params.beginTime},'%y%m%d')


		AND date_format(a.buy_date,'%y%m%d') <=
		date_format(#{params.endTime},'%y%m%d')

	 and a.assets_status = #{assetsStatus}
	 and a.use_status = #{useStatus}
	 and a.use_time = #{useTime}
	 and a.submit_type = #{submitType}

ORDER BY create_time DESC

mapper.update:
update xz_asstes
assets_code = #{assetsCode}, region = #{region}, assets_name = #{assetsName}, assets_type = #{assetsType}, assets_type2 = #{assetsType2}, assets_model = #{assetsModel}, price = #{price}, unit = #{unit}, count = #{count}, attach = #{attach}, purchase_num = #{purchaseNum}, category = #{category}, brand = #{brand}, buy_address = #{buyAddress}, manufacturer = #{manufacturer}, supplier = #{supplier}, invoice_type = #{invoiceType}, invoice_num = #{invoiceNum}, purchase_by = #{purchaseBy}, buy_date = #{buyDate}, annex = #{annex}, remarks = #{remarks}, extend_money = #{extendMoney}, extend_time = #{extendTime}, extend_content = #{extendContent}, assets_status = #{assetsStatus}, use_status = #{useStatus}, use_by = #{useBy}, department = #{department}, use_time = #{useTime}, submit_type = #{submitType}, scrap_date = #{scrapDate}, store_way = #{storeWay}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, sub_by = #{subBy}, sub_time = #{subTime},
where id = #{id}

update xz_asstes
use_status = '3', use_time = #{useTime},
where id=#{id}

update xz_asstes
use_status = '5', use_time = #{useTime},
where id=#{id}

mapper.select:

where a.id in (
	select min(b.id) as id 
	  from (select a.id 
			  from (select * 
			  		  from xz_asstes
					where 
					assets_type2 = #{assetsType2}
					 and region = #{region}
					and assets_status='2'
				    and use_status ='1' 
					and sort ='2'
					) a 
			) b
)
