mapper.update:
update xz_expense_record
expense_name = #{expenseName}, expense_type_parent = #{expenseTypeParent}, expense_type = #{expenseType}, region = #{region}, dev_desc = #{devDesc}, purchase_channel = #{purchaseChannel}, unit_price = #{unitPrice}, count = #{count}, mark_date = #{markDate}, use_date = #{useDate}, mark_user = #{markUser}, apply_user = #{applyUser}, comment = #{comment}, submit_type = #{submitType}, place = #{place}, end_date = #{endDate}, start_date = #{startDate}, active_name = #{activeName}, sum_price = #{sumPrice}, resource_id = #{resourceId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select t1.expense_type_parent, t1.expense_type_parent_name, t1.expense_type, t1.expense_type_name, t1.year, ifnull(t1.month1,0)-ifnull(t2.month1,0) month1, ifnull(t1.month2,0)-ifnull(t2.month2,0) month2, ifnull(t1.month3,0)-ifnull(t2.month3,0) month3, ifnull(t1.month4,0)-ifnull(t2.month4,0) month4, ifnull(t1.month5,0)-ifnull(t2.month5,0) month5, ifnull(t1.month6,0)-ifnull(t2.month6,0) month6, ifnull(t1.month7,0)-ifnull(t2.month7,0) month7, ifnull(t1.month8,0)-ifnull(t2.month8,0) month8, ifnull(t1.month9,0)-ifnull(t2.month9,0) month9, ifnull(t1.month10,0)-ifnull(t2.month10,0) month10, ifnull(t1.month11,0)-ifnull(t2.month11,0) month11, ifnull(t1.month12,0)-ifnull(t2.month12,0) month12, ifnull(t1.sum,0)-ifnull(t2.sum,0) sum from (
select a.region,a.expense_type_parent, a.expense_type_parent_name, a.expense_type, a.expense_type_name, a.year, a.month1, a.month2, a.month3, a.month4, a.month5, a.month6, a.month7, a.month8, a.month9, a.month10, a.month11, a.month12, (a.month1+a.month2+a.month3+a.month4+a.month5+a.month6+a.month7+ a.month8+a.month9+a.month10+a.month11+a.month12) sum from (SELECT er.expense_type_parent,rt.name expense_type_parent_name, er.expense_type,xrt.name expense_type_name,YEAR(use_date) as year, sum(CASE WHEN MONTH(er.use_date) =1 THEN er.sum_price ELSE 0 END) month1, sum(CASE WHEN MONTH(er.use_date) =2 THEN er.sum_price ELSE 0 END) month2, sum(CASE WHEN MONTH(er.use_date) =3 THEN er.sum_price ELSE 0 END) month3, sum(CASE WHEN MONTH(er.use_date) =4 THEN er.sum_price ELSE 0 END) month4, sum(CASE WHEN MONTH(er.use_date) =5 THEN er.sum_price ELSE 0 END) month5, sum(CASE WHEN MONTH(er.use_date) =6 THEN er.sum_price ELSE 0 END) month6, sum(CASE WHEN MONTH(er.use_date) =7 THEN er.sum_price ELSE 0 END) month7, sum(CASE WHEN MONTH(er.use_date) =8 THEN er.sum_price ELSE 0 END) month8, sum(CASE WHEN MONTH(er.use_date) =9 THEN er.sum_price ELSE 0 END) month9, sum(CASE WHEN MONTH(er.use_date) =10 THEN er.sum_price ELSE 0 END) month10, sum(CASE WHEN MONTH(er.use_date) =11 THEN er.sum_price ELSE 0 END) month11, sum(CASE WHEN MONTH(er.use_date) =12 THEN er.sum_price ELSE 0 END) month12, er.region,er.use_date FROM xz_expense_record er LEFT JOIN xz_resource_type rt on rt.id=er.expense_type_parent LEFT JOIN xz_resource_type xrt on xrt.id=er.expense_type LEFT JOIN sys_user b on er.apply_user = b.user_id where er.submit_type='2'
AND date_format(er.use_date,'%y%m%d') >= date_format(#{xzExpenseSta.params.beginTime},'%y%m%d')
AND date_format(er.use_date,'%y%m%d') <= date_format(#{xzExpenseSta.params.endTime},'%y%m%d')
and b.dept_id=#{xzExpenseSta.dept} and b.dept_id in
#{item}
GROUP BY er.expense_type_parent)a GROUP BY er.region, er.expense_type_parent)a
and a.region = #{xzExpenseSta.region}
) t1 left join ( select a.region,a.expense_type_parent, a.expense_type_parent_name, a.expense_type, a.expense_type_name, a.year, a.month1, a.month2, a.month3, a.month4, a.month5, a.month6, a.month7, a.month8, a.month9, a.month10, a.month11, a.month12, (a.month1+a.month2+a.month3+a.month4+a.month5+a.month6+a.month7+ a.month8+a.month9+a.month10+a.month11+a.month12) sum from (SELECT er.expense_type_parent,rt.name expense_type_parent_name, er.expense_type,xrt.name expense_type_name,YEAR(use_date) as year, sum(CASE WHEN MONTH(er.use_date) =1 THEN er.sum_price ELSE 0 END) month1, sum(CASE WHEN MONTH(er.use_date) =2 THEN er.sum_price ELSE 0 END) month2, sum(CASE WHEN MONTH(er.use_date) =3 THEN er.sum_price ELSE 0 END) month3, sum(CASE WHEN MONTH(er.use_date) =4 THEN er.sum_price ELSE 0 END) month4, sum(CASE WHEN MONTH(er.use_date) =5 THEN er.sum_price ELSE 0 END) month5, sum(CASE WHEN MONTH(er.use_date) =6 THEN er.sum_price ELSE 0 END) month6, sum(CASE WHEN MONTH(er.use_date) =7 THEN er.sum_price ELSE 0 END) month7, sum(CASE WHEN MONTH(er.use_date) =8 THEN er.sum_price ELSE 0 END) month8, sum(CASE WHEN MONTH(er.use_date) =9 THEN er.sum_price ELSE 0 END) month9, sum(CASE WHEN MONTH(er.use_date) =10 THEN er.sum_price ELSE 0 END) month10, sum(CASE WHEN MONTH(er.use_date) =11 THEN er.sum_price ELSE 0 END) month11, sum(CASE WHEN MONTH(er.use_date) =12 THEN er.sum_price ELSE 0 END) month12, er.region,er.use_date FROM xz_expense_record er LEFT JOIN xz_resource_type rt on rt.id=er.expense_type_parent LEFT JOIN xz_resource_type xrt on xrt.id=er.expense_type LEFT JOIN sys_user b on er.apply_user = b.user_id where er.submit_type='2'
AND date_format(er.use_date,'%y%m%d') >= date_format(#{compareData.paramsCompare.beginTime},'%y%m%d')
AND date_format(er.use_date,'%y%m%d') <= date_format(#{compareData.paramsCompare.endTime},'%y%m%d')
and b.dept_id in
#{item}
GROUP BY er.expense_type_parent)a GROUP BY er.region, er.expense_type_parent)a
and a.region = #{compareData.regionCompare}
)t2 on t1.expense_type_parent = t2.expense_type_parent and t1.expense_type = t2.expense_type

