mapper.select:
select er.id, er.expense_name, er.expense_type_parent, er.expense_type, er.region, er.dev_desc, er.purchase_channel, er.unit_price, er.count, er.mark_date, er.use_date, er.mark_user, er.apply_user, er.comment, er.submit_type, er.place, er.end_date, er.start_date, er.active_name, er.sum_price, er.resource_id, er.create_by, er.create_time, er.update_by, er.update_time, u.user_name apply_user_name, rt.name expense_type_name from xz_expense_record er left join sys_user u on u.user_id= er.apply_user left join xz_resource_type rt on rt.id=er.expense_type left join xz_resource_type xrt on xrt.id=er.expense_type_parent
and rt.id = #{expenseType} and xrt.id = #{expenseTypeParent} and er.expense_name like concat('%' ,#{expenseName} ,'%') and er.region = #{region} and u.user_name like concat('%' ,#{applyUserName} ,'%') and er.useDate = #{useDate}
ORDER BY er.use_date DESC

mapper.update:
update xz_expense_record
expense_name = #{expenseName}, expense_type_parent = #{expenseTypeParent}, expense_type = #{expenseType}, region = #{region}, dev_desc = #{devDesc}, purchase_channel = #{purchaseChannel}, unit_price = #{unitPrice}, count = #{count}, mark_date = #{markDate}, use_date = #{useDate}, mark_user = #{markUser}, apply_user = #{applyUser}, comment = #{comment}, submit_type = #{submitType}, place = #{place}, end_date = #{endDate}, start_date = #{startDate}, active_name = #{activeName}, sum_price = #{sumPrice}, resource_id = #{resourceId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
