mapper.select:
SELECT a.id, a.num, a.name, c.`dept_name` , a.`type`, a.amount, a.reason, b.user_name, a.`create_time`, a.pass_time, a.`fac_operate`, a.`status`, a.reimburse_time, a.update_time, a.apply_status, a.submit_status FROM fac_reimburse_apply AS a LEFT JOIN sys_user AS b ON a.loan_user = b.user_id LEFT JOIN sys_dept AS c ON c.dept_id = b.dept_id where 1=1
and a.name = #{name}
and a.id = #{id} and a.num=#{num} and a.create_by=#{createBy} and a.loan_user=#{loanUser} order by a.create_time desc

select * from  fac_rei_hospitality_apply

     and id = #{id}
      and dd_date = #{ddDate}
      and amount = #{amount}
      and add_user = #{addUser}
      and document_num = #{documentNum}
      and location = #{location}
      and target_unit = #{targetUnit}
      and reason = #{reason}
      and num = #{num}
      and status = #{status}
      and apply_num = #{applyNum}
      and user = #{user}


select ifNull((select
sum(amount) from fac_traffic_rei_apply where num
=#{num}),0);


select ifNull((select
sum(amount) from fac_rei_hospitality_apply where num
=#{num}),0);

mapper.update:
update fac_reimburse_apply
name = #{name}, amount = #{amount}, loan_user = #{loanUser}, create_time = #{createTime}, reimburse_time = #{reimburseTime}, fac_operate = #{facOperate}, reason = #{reason}, apply_status = #{applyStatus}, update_time = #{updateTime}, pass_time = #{passTime}, type = #{type}, status = #{status},
where num = #{num}

update fac_reimburse_apply
num = #{num}, name = #{name}, amount = #{amount}, loan_user = #{loanUser}, create_time = #{createTime}, reimburse_time = #{reimburseTime}, fac_operate = #{facOperate}, reason = #{reason}, apply_status = #{applyStatus}, update_time = #{updateTime}, pass_time = #{passTime}, type = #{type},
where id = #{id}

update fac_traffic_rei_apply
dd_date = #{ddDate}, target_unit = #{targetUnit}, amount = #{amount}, user = #{user}, departure = #{departure}, target = #{target}, apply_user = #{applyUser}, document_num = #{documentNum}, reason = #{reason}, type = #{type}, num = #{num}, status = #{status},
where id = #{id}

mapper.select:
SELECT a.id, a.num, a.name, c.`dept_name` , a.`type`, a.amount, a.reason, b.user_name, a.`create_time`, a.pass_time, a.`fac_operate`, a.`status`, a.reimburse_time, a.update_time, a.apply_status, a.submit_status FROM fac_reimburse_apply AS a LEFT JOIN sys_user AS b ON a.loan_user = b.user_id LEFT JOIN sys_dept AS c ON c.dept_id = b.dept_id where a.create_by in
#{item.userId}
order by a.create_time desc
